com.webreach.mirth.model.Step.script:
//Convierte el HL7 v2.3 de adminisiones en HL7-XML v2.3
var procDir = globalMap.get("procDir");
var originalFilename = channelMap.get("originalFilename");	

var msgRawData = messageObject.getRawData()
var xmlMsg = SerializerFactory.getHL7Serializer(true, false, false).toXML(msgRawData);

channelMap.put("xmlMsg", xmlMsg);
FileUtil.write(procDir + "/" + originalFilename + ".xml", false, xmlMsg);

entry.string:
//Convierte el HL7 v2.3 de adminisiones en HL7-XML v2.3
var procDir = globalMap.get("procDir");
var originalFilename = channelMap.get("originalFilename");	

var msgRawData = messageObject.getRawData()
var xmlMsg = SerializerFactory.getHL7Serializer(true, false, false).toXML(msgRawData);

channelMap.put("xmlMsg", xmlMsg);
FileUtil.write(procDir + "/" + originalFilename + ".xml", false, xmlMsg);

var originalFilename = channelMap.get("originalFilename");
var controller = globalMap.get("controller");
var extension = globalMap.get("admFilesExt");

var str_originalFilename = new Packages.java.lang.String(originalFilename);
var str_extension = new Packages.java.lang.String(extension);

if(controller.hasErrors()){
	return false;	
}else if( !Packages.org.apache.commons.io.FilenameUtils.isExtension(str_originalFilename, str_extension) ) {
	return false;
}else if(messageObject.getType() == "ADT-A17"){

	var sendDir = globalMap.get("sendDir");	
	var procDir = globalMap.get("procDir");

	//controller.addError(-1);
	Packages.org.delfos.mirth.utils.MirthFileUtils.moveFileToDirectory(procDir + "/" + originalFilename, sendDir);	
	return false;	
}else {
	return true;
}

