com.webreach.mirth.model.Step.script:
//Convierte el HL7-XML v2.3 en HL7-XML v.2.5 para enviar a Silicon
var procDir = globalMap.get("procDir");
var originalFilename = channelMap.get("originalFilename");	

var xmlSiliconMsg;
var converter;

try{

	converter = Packages.org.delfos.mirth.hie.HL72SiliconFactory.getHL72SiliconConverter($('_msg_type'));
	xmlSiliconMsg = converter.convert($('xmlMsg'));	

}catch(ex){ //Se utiliza el conversor por defecto

	var defaultConverter = Packages.org.delfos.mirth.hie.HL72SiliconFactory.DEFAULT;
	converter = Packages.org.delfos.mirth.hie.HL72SiliconFactory.getHL72SiliconConverter(defaultConverter);
	xmlSiliconMsg = converter.convert($('xmlMsg'));	
}

channelMap.put("xmlSiliconMsg",xmlSiliconMsg);
FileUtil.write(procDir + "/" + originalFilename + ".silicon", false, xmlSiliconMsg);

