com.webreach.mirth.model.Channel.postprocessingScript:
var siliconWS = responseMap.get("SiliconWS");
var controller = globalMap.get("controller");
var procDir = globalMap.get("procDir");

//Si no se ha filtrado el mensaje y se ha enviado a Silicon
if (siliconWS != null && siliconWS.getStatus() == "SUCCESS"){ //El mensaje se ha enviado a Silicon


	var sendDir = globalMap.get("sendDir");	
	var hookFile = globalMap.get("hookFile").toString();
	var originalFilename = channelMap.get("originalFilename");	

	//Copia los mensajes HL7 generados en el directorio de enviados
	Packages.org.delfos.mirth.utils.MirthFileUtils.moveFileToDirectory(procDir + "/" + originalFilename, sendDir);	
	Packages.org.delfos.mirth.utils.MirthFileUtils.moveFileToDirectory(procDir + "/" + originalFilename + ".xml", sendDir);	
	Packages.org.delfos.mirth.utils.MirthFileUtils.moveFileToDirectory(procDir + "/" + originalFilename + ".silicon", sendDir);	

}else if (siliconWS != null){ //Si no se ha filtrado el mensaje pero no se ha podido enviar a Silicon
	controller.addError(controller.getCounter());
}

try{
	controller.next();
}catch(e){
	controller.processMessages();
}

//Elimina los ficheros no válidos del directorio de procesado de ficheros
var validFileExts = [globalMap.get("admFilesExt"), globalMap.get("hookFileExt")];
Packages.org.delfos.mirth.utils.MirthFileUtils.purgeDirectory(procDir, validFileExts);	

return;

com.webreach.mirth.model.Channel.deployScript:
//Parametrización del controlador del canal

//Directorio donde admisiones deja los ficheros HL7
var srcDir = "E:/novale";

//Directorio de procesado de ficheros HL7
var procDir = "E:/novale/procdir";
globalMap.put("procDir", procDir);

//Extensión de los ficheros de admisiones
var admFilesExt = "ADM";
globalMap.put("admFilesExt", admFilesExt);

//Directorio donde se encuentra el fichero anzuelo
var hookDir = "E:/novale/hookdir";

//Fichero anzuelo. No puede tener la extensión "ADM" y ha de ser un HL7 válido
var hookFile = "file.hook";
globalMap.put("hookFile", hookFile);

//Extensión del fichero anzuelo
var hookFileExt = "hook";
globalMap.put("hookFileExt", hookFileExt);

//Directorio donde se dejan los mensajes enviados a Silicon
var sendDir = "E:/novale/sendDir";
globalMap.put("sendDir", sendDir);

//Instancia del controlador del canal
var controller = new Packages.org.delfos.mirth.utils.HIEMessagesController(srcDir, procDir, hookDir + "/" + hookFile, 4);

//Se arranca el procesado de mensajes
controller.processMessages();

//Añade la instancia del controllador
globalMap.put("controller",controller);

//Se arranca la tarea de limpieza de ficheros
var springConfFile = "E:/aplicaciones/mirth/conf/custom/spring_mirth_hie.xml";
var springContext = new Packages.org.springframework.context.support.FileSystemXmlApplicationContext(springConfFile);

globalMap.put("springContextFile", springContext);


return;
